	public String converToSimple(String ss)
	{
		StringBuffer sb = new StringBuffer();
		//StringBuffer sbt = new StringBuffer();
		for (int i = 0; i < ss.length(); i++) {
			char temp = ss.charAt(i);
			//System.out.println("temp=" + temp + ",index=" + traditional.indexOf(temp));
			if (traditional.indexOf(temp) != -1)
			{
				sb.append(simple.charAt(traditional.indexOf(temp)));
				//sbt.append(traditional.charAt(traditional.indexOf(temp)));
			}
			else
				sb.append(temp);
		}
		//System.out.println("change=" + sbt.toString());
		return sb.toString();
	}

	public String converToTraditional(String ss)
	{
		StringBuffer sb = new StringBuffer();
		for (int i = 0; i < ss.length(); i++) {
			char temp = ss.charAt(i);
			if (simple.indexOf(temp) != -1)
				sb.append(traditional.charAt(simple.indexOf(temp)));
			else
				sb.append(temp);
		}
		return sb.toString();
	}
	
	public boolean isContainTraditional(String st) {
		for (int i = 0; i < st.length(); i++) {
			char temp = st.charAt(i);
			if (traditional.indexOf(temp) != -1)
				return true;
		}
		return false;
	}

