	public void getDiff(){
		StringBuffer sb1 = new StringBuffer();
		StringBuffer sb2 = new StringBuffer();
		for (int i = 0; i < traditional.length(); i++) {
			if (traditional.charAt(i) != simple.charAt(i))
			{
				sb1.append(traditional.charAt(i));
				sb2.append(simple.charAt(i));
			}
		}
		System.out.println(sb1.toString());
		System.out.println(sb2.toString());
	}
	
	public static void main(String[] args) {
		ChineseTraditional trad = new ChineseTraditional();
		System.out.println(trad.converToTraditional("滋养秀发"));
	}
